  <style>
    .phpdebugbar pre.sf-dump {
      display: block;
      white-space: pre;
      padding: 5px;
      overflow: initial !important;
    }

    .phpdebugbar pre.sf-dump:after {
      content: "";
      visibility: hidden;
      display: block;
      height: 0;
      clear: both;
    }

    .phpdebugbar pre.sf-dump span {
      display: inline;
    }

    .phpdebugbar pre.sf-dump .sf-dump-compact {
      display: none;
    }

    .phpdebugbar pre.sf-dump a {
      text-decoration: none;
      cursor: pointer;
      border: 0;
      outline: none;
      color: inherit;
    }

    .phpdebugbar pre.sf-dump img {
      max-width: 50em;
      max-height: 50em;
      margin: .5em 0 0 0;
      padding: 0;
      background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAAAAAA6mKC9AAAAHUlEQVQY02O8zAABilCaiQEN0EeA8QuUcX9g3QEAAjcC5piyhyEAAAAASUVORK5CYII=) #D3D3D3;
    }

    .phpdebugbar pre.sf-dump .sf-dump-ellipsis {
      display: inline-block;
      overflow: visible;
      text-overflow: ellipsis;
      max-width: 5em;
      white-space: nowrap;
      overflow: hidden;
      vertical-align: top;
    }

    .phpdebugbar pre.sf-dump .sf-dump-ellipsis+.sf-dump-ellipsis {
      max-width: none;
    }

    .phpdebugbar pre.sf-dump code {
      display: inline;
      padding: 0;
      background: none;
    }

    .sf-dump-str-collapse .sf-dump-str-collapse {
      display: none;
    }

    .sf-dump-str-expand .sf-dump-str-expand {
      display: none;
    }

    .sf-dump-public.sf-dump-highlight,
    .sf-dump-protected.sf-dump-highlight,
    .sf-dump-private.sf-dump-highlight,
    .sf-dump-str.sf-dump-highlight,
    .sf-dump-key.sf-dump-highlight {
      background: rgba(111, 172, 204, 0.3);
      border: 1px solid #7DA0B1;
      border-radius: 3px;
    }

    .sf-dump-public.sf-dump-highlight-active,
    .sf-dump-protected.sf-dump-highlight-active,
    .sf-dump-private.sf-dump-highlight-active,
    .sf-dump-str.sf-dump-highlight-active,
    .sf-dump-key.sf-dump-highlight-active {
      background: rgba(253, 175, 0, 0.4);
      border: 1px solid #ffa500;
      border-radius: 3px;
    }

    .phpdebugbar pre.sf-dump .sf-dump-search-hidden {
      display: none !important;
    }

    .phpdebugbar pre.sf-dump .sf-dump-search-wrapper {
      font-size: 0;
      white-space: nowrap;
      margin-bottom: 5px;
      display: flex;
      position: -webkit-sticky;
      position: sticky;
      top: 5px;
    }

    .phpdebugbar pre.sf-dump .sf-dump-search-wrapper>* {
      vertical-align: top;
      box-sizing: border-box;
      height: 21px;
      font-weight: normal;
      border-radius: 0;
      background: #FFF;
      color: #757575;
      border: 1px solid #BBB;
    }

    .phpdebugbar pre.sf-dump .sf-dump-search-wrapper>input.sf-dump-search-input {
      padding: 3px;
      height: 21px;
      font-size: 12px;
      border-right: none;
      border-top-left-radius: 3px;
      border-bottom-left-radius: 3px;
      color: #000;
      min-width: 15px;
